--------------------------------------------------------
--  DDL for View CERT_VIEW_TOFILE
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "CERT_VIEW_TOFILE" ("CERT_ID", "FORMS", "UNN", "KONTRP", "KONTRS", "ADRESS", "POLUCHAT", "ADRESSPOL", "DATACERT", "ISSUEDATE", "NOMERCERT", "EXPERT", "NBLANKA", "RUKOVOD", "TRANSPORT", "MARSHRUT", "OTMETKA", "STRANAV", "STRANAPR", "STATUS", "KOLDOPLIST", "FLEXP", "UNNEXP", "EXPP", "EXPS", "EXPADRESS", "FLIMP", "IMPORTER", "ADRESSIMP", "FLSEZ", "SEZ", "FLSEZREZ", "STRANAP", "OTD_ID", "PARENTNUMBER", "PARENTSTATUS", "DENORM", "PARENT_ID", "OTD_NAME", "NAME", "OTD_ADDRESS_INDEX", "ADDR_CITY", "ADDR_LINE", "ADDR_BUILDING", "TOVAR") AS 
  SELECT cert."CERT_ID",cert."FORMS",cert."UNN",cert."KONTRP",cert."KONTRS",cert."ADRESS",cert."POLUCHAT",cert."ADRESSPOL",cert."DATACERT",cert."ISSUEDATE",cert."NOMERCERT",cert."EXPERT",cert."NBLANKA",cert."RUKOVOD",cert."TRANSPORT",cert."MARSHRUT",cert."OTMETKA",cert."STRANAV",cert."STRANAPR",cert."STATUS",cert."KOLDOPLIST",cert."FLEXP",cert."UNNEXP",cert."EXPP",cert."EXPS",cert."EXPADRESS",cert."FLIMP",cert."IMPORTER",cert."ADRESSIMP",cert."FLSEZ",cert."SEZ",cert."FLSEZREZ",cert."STRANAP",cert."OTD_ID",cert."PARENTNUMBER",cert."PARENTSTATUS",cert."DENORM",cert."PARENT_ID",cert."OTD_NAME",cert."NAME",cert."OTD_ADDRESS_INDEX",cert."ADDR_CITY",cert."ADDR_LINE",cert."ADDR_BUILDING", a.tovar FROM CERT_VIEW cert INNER JOIN C_PRODUCT_DENORM a on cert.CERT_ID=a.CERT_ID;
